mapper.insert:
INSERT INTO disaster_event_live (
    region_code, disaster_type, title, location, magnitude, depth_km, 
    alert_level, status, started_at, source
) VALUES (
    #{regionCode}, #{disasterType}, #{title}, #{location}, #{magnitude}, 
    #{depthKm}, #{alertLevel}, #{status}, #{startedAt}, #{source}
)

INSERT INTO disaster_history (
    disaster_type, title, location, magnitude, depth_km, 
    alert_level, description, occurred_at, source
) VALUES (
    #{disasterType}, #{title}, #{location}, #{magnitude}, 
    #{depthKm}, #{alertLevel}, #{description}, #{occurredAt}, #{source}
)

mapper.select:
SELECT * FROM disaster_history 
ORDER BY occurred_at DESC 
LIMIT #{limit}

SELECT COUNT(*) > 0 FROM disaster_history 
WHERE disaster_type = #{disasterType}
AND location = #{location}
AND ABS(TIMESTAMPDIFF(MINUTE, occurred_at, #{occurredAt})) < 30

    AND magnitude IS NOT NULL 
    AND ABS(magnitude - #{magnitude}) < 0.3
